var fs = require('fs'),
    http = require('http'),
    spdy = require('../../');

var options = {
  plain: 'spdy/2'
};

var server = spdy.createServer(http.Server, options, function(req, response) {
  response.writeHead(200, {
    "Content-Type": "text/plain"
  });
  response.end("Hello World!\n");
});

server.listen(3232);
